function convertDateString(input) {
    // Create a new Date object from the input string
    const date = new Date(input);

    // Extract the year, month, and day parts
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
    const day = String(date.getDate()).padStart(2, '0');

    // Combine the parts into the desired format
    return `${year}-${month}-${day}`;
}

function set_underscore(document) {

  const firstPart = document.slice(0, -1);
  const lastCharacter = document.slice(-1);

  return `${firstPart}-${lastCharacter}`;

}

for (const item of $input.all()) {
  const emisor = set_underscore(item.json.numero_documento_emisor);
  const pagador = set_underscore(item.json.numero_documento_pagador);
  item.json.numero_documento_emisor = emisor;
  item.json.numero_documento_pagador = pagador;

  const output = convertDateString(item.json.fecha_emision);
  item.json.fecha_emision = output;

}

return $input.all();

