function removeDuplicateEnterprise(enterprises) {
    // Create a new Map to hold unique users by id
    const enterpriseMap = new Map();

    enterprises.forEach(enterprise => {
        // Use the user's id as the key in the Map
        enterpriseMap.set(enterprise.id, enterprise);
    });

    // Convert the Map values back to an array
    return Array.from(enterpriseMap.values());
}


