// Loop over input items and add a new field called 'myNewField' to the JSON of each one

let text = "";
let messages = []
for (const item of $input.all()) {
  if(item.json.success){
    return []
  }
  //our pizza delivery *has arrived*!\nThank you for using _Cymbal Pizza!_
  messages.push(`La operación ${item.json.operation_id} existe ${item.json.cuenta} veces.`)
}

return {text:messages.join('\n')};

